targets.expr:
sum(
  round(
    increase(
      celery_task_succeeded_total{job=~".*celery-exporter.*"}[$__range]
    )
  )
)
/(sum(
  round(
    increase(
      celery_task_succeeded_total{job=~".*celery-exporter.*"}[$__range]
    )
  )
)
+sum(
  round(
    increase(
      celery_task_failed_total{job=~".*celery-exporter.*"}[$__range]
    )
  )
)
)

sum (
  round(
    increase(
      celery_task_succeeded_total{job=~".*celery-exporter.*"}[$__range]
    )
  )
) by (job)
/(sum (
  round(
    increase(
      celery_task_succeeded_total{job=~".*celery-exporter.*"}[$__range]
    )
  )
) by (job)
+sum (
  round(
    increase(
      celery_task_failed_total{job=~".*celery-exporter.*"}[$__range]
    )
  )
) by (job)
) > -1

sum (
  rate(
    celery_task_runtime_sum{job=~".*celery-exporter.*"}[$__range]
  )
) by(name)
/
sum (
  rate(
    celery_task_runtime_count{job=~".*celery-exporter.*"}[$__range]
  )
) by (name) > 0
